import{_ as l,o as i,c as e,ag as t}from"./chunks/framework.CbQjVMS6.js";const g=JSON.parse('{"title":"快速开始","description":"","frontmatter":{},"headers":[],"relativePath":"guide/getting-started.md","filePath":"guide/getting-started.md","lastUpdated":1769581884000}'),r={name:"guide/getting-started.md"};function o(s,a,h,n,d,c){return i(),e("div",null,[...a[0]||(a[0]=[t('<h1 id="快速开始" tabindex="-1">快速开始 <a class="header-anchor" href="#快速开始" aria-label="Permalink to &quot;快速开始&quot;">​</a></h1><h2 id="下载安装" tabindex="-1">下载安装 <a class="header-anchor" href="#下载安装" aria-label="Permalink to &quot;下载安装&quot;">​</a></h2><h3 id="系统要求" tabindex="-1">系统要求 <a class="header-anchor" href="#系统要求" aria-label="Permalink to &quot;系统要求&quot;">​</a></h3><ul><li>Windows 10/11 64位</li><li>.NET Framework 4.8+</li><li>USB 驱动（高通9008/MTK/展锐）</li></ul><h3 id="下载地址" tabindex="-1">下载地址 <a class="header-anchor" href="#下载地址" aria-label="Permalink to &quot;下载地址&quot;">​</a></h3><ul><li><strong>官方下载</strong>: <a href="https://github.com/xiriovo/MultiFlash-Tool/releases" target="_blank" rel="noreferrer">GitHub Releases</a></li><li><strong>蓝奏云</strong>: <a href="https://www.lanzoui.com/multiflash" target="_blank" rel="noreferrer">点击下载</a></li></ul><h3 id="安装步骤" tabindex="-1">安装步骤 <a class="header-anchor" href="#安装步骤" aria-label="Permalink to &quot;安装步骤&quot;">​</a></h3><ol><li>下载 <code>SakuraEDL-Setup-v3.0.exe</code></li><li>双击运行安装程序</li><li>按提示完成安装</li><li>安装 USB 驱动（首次使用）</li></ol><hr><h2 id="驱动安装" tabindex="-1">驱动安装 <a class="header-anchor" href="#驱动安装" aria-label="Permalink to &quot;驱动安装&quot;">​</a></h2><h3 id="高通-9008-驱动" tabindex="-1">高通 9008 驱动 <a class="header-anchor" href="#高通-9008-驱动" aria-label="Permalink to &quot;高通 9008 驱动&quot;">​</a></h3><ol><li>下载 <a href="https://androiddatahost.com/qualcomm-hs-usb-qdloader-9008-driver/" target="_blank" rel="noreferrer">Qualcomm HS-USB Driver</a></li><li>以管理员身份运行安装</li><li>重启电脑</li></ol><h3 id="mtk-驱动" tabindex="-1">MTK 驱动 <a class="header-anchor" href="#mtk-驱动" aria-label="Permalink to &quot;MTK 驱动&quot;">​</a></h3><ol><li>下载 <a href="https://androiddatahost.com/mtk-usb-all-drivers/" target="_blank" rel="noreferrer">MTK USB VCOM Driver</a></li><li>安装 <code>MTK_USB_Driver_v1.0.8.zip</code></li><li>重启电脑</li></ol><h3 id="展锐驱动" tabindex="-1">展锐驱动 <a class="header-anchor" href="#展锐驱动" aria-label="Permalink to &quot;展锐驱动&quot;">​</a></h3><ol><li>下载 <a href="https://androiddatahost.com/spreadtrum-usb-drivers/" target="_blank" rel="noreferrer">Spreadtrum USB Driver</a></li><li>安装驱动</li><li>重启电脑</li></ol><hr><h2 id="进入刷机模式" tabindex="-1">进入刷机模式 <a class="header-anchor" href="#进入刷机模式" aria-label="Permalink to &quot;进入刷机模式&quot;">​</a></h2><h3 id="高通-edl-模式-9008" tabindex="-1">高通 EDL 模式 (9008) <a class="header-anchor" href="#高通-edl-模式-9008" aria-label="Permalink to &quot;高通 EDL 模式 (9008)&quot;">​</a></h3><p><strong>方法一：组合键</strong></p><ol><li>关机</li><li>同时按住 <strong>音量+</strong> + <strong>音量-</strong></li><li>插入 USB 数据线</li><li>设备管理器显示 <code>Qualcomm HS-USB QDLoader 9008</code></li></ol><p><strong>方法二：ADB 命令</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">adb</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> reboot</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> edl</span></span></code></pre></div><p><strong>方法三：Fastboot 命令</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">fastboot</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oem</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> edl</span></span></code></pre></div><h3 id="mtk-brom-模式" tabindex="-1">MTK BROM 模式 <a class="header-anchor" href="#mtk-brom-模式" aria-label="Permalink to &quot;MTK BROM 模式&quot;">​</a></h3><ol><li>关机并取出电池（如可拆卸）</li><li>按住 <strong>音量+</strong> 或 <strong>音量-</strong></li><li>插入 USB 数据线</li><li>设备管理器显示 <code>MediaTek USB Port</code></li></ol><h3 id="展锐下载模式" tabindex="-1">展锐下载模式 <a class="header-anchor" href="#展锐下载模式" aria-label="Permalink to &quot;展锐下载模式&quot;">​</a></h3><ol><li>关机</li><li>按住 <strong>音量-</strong></li><li>插入 USB 数据线</li><li>设备管理器显示 <code>SPRD U2S Diag</code></li></ol><h3 id="fastboot-模式" tabindex="-1">Fastboot 模式 <a class="header-anchor" href="#fastboot-模式" aria-label="Permalink to &quot;Fastboot 模式&quot;">​</a></h3><ol><li>关机</li><li>按住 <strong>音量-</strong> + <strong>电源键</strong></li><li>出现 Fastboot 界面后松开</li></ol><hr><h2 id="基本使用" tabindex="-1">基本使用 <a class="header-anchor" href="#基本使用" aria-label="Permalink to &quot;基本使用&quot;">​</a></h2><h3 id="_1-选择平台" tabindex="-1">1. 选择平台 <a class="header-anchor" href="#_1-选择平台" aria-label="Permalink to &quot;1. 选择平台&quot;">​</a></h3><p>打开 SakuraEDL，根据你的设备选择对应平台：</p><ul><li><strong>Qualcomm</strong> - 高通芯片设备</li><li><strong>MediaTek</strong> - 联发科芯片设备</li><li><strong>Spreadtrum</strong> - 展锐芯片设备</li><li><strong>Fastboot</strong> - Fastboot 模式</li></ul><h3 id="_2-连接设备" tabindex="-1">2. 连接设备 <a class="header-anchor" href="#_2-连接设备" aria-label="Permalink to &quot;2. 连接设备&quot;">​</a></h3><ol><li>让设备进入对应的刷机模式</li><li>软件会自动检测到设备</li><li>显示设备信息</li></ol><h3 id="_3-选择操作" tabindex="-1">3. 选择操作 <a class="header-anchor" href="#_3-选择操作" aria-label="Permalink to &quot;3. 选择操作&quot;">​</a></h3><p>根据需要选择：</p><ul><li><strong>刷写分区</strong> - 刷入指定镜像</li><li><strong>读取分区</strong> - 备份分区数据</li><li><strong>擦除分区</strong> - 清除分区数据</li><li><strong>解锁</strong> - 解锁 Bootloader</li></ul><h3 id="_4-执行操作" tabindex="-1">4. 执行操作 <a class="header-anchor" href="#_4-执行操作" aria-label="Permalink to &quot;4. 执行操作&quot;">​</a></h3><ol><li>选择要刷写的文件</li><li>点击开始</li><li>等待完成</li></ol><hr><h2 id="下一步" tabindex="-1">下一步 <a class="header-anchor" href="#下一步" aria-label="Permalink to &quot;下一步&quot;">​</a></h2><ul><li><a href="/guide/qualcomm.html">高通 EDL 详细教程</a></li><li><a href="/guide/mtk.html">MTK 详细教程</a></li><li><a href="/guide/spd.html">展锐详细教程</a></li><li><a href="/guide/fastboot.html">Fastboot 详细教程</a></li></ul>',46)])])}const b=l(r,[["render",o]]);export{g as __pageData,b as default};
