import{_ as l,o as t,c as i,ag as e}from"./chunks/framework.CbQjVMS6.js";const b=JSON.parse('{"title":"MTK 联发科教程","description":"","frontmatter":{},"headers":[],"relativePath":"guide/mtk.md","filePath":"guide/mtk.md","lastUpdated":null}'),o={name:"guide/mtk.md"};function r(d,a,h,s,n,c){return t(),i("div",null,[...a[0]||(a[0]=[e('<h1 id="mtk-联发科教程" tabindex="-1">MTK 联发科教程 <a class="header-anchor" href="#mtk-联发科教程" aria-label="Permalink to &quot;MTK 联发科教程&quot;">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p>MTK (MediaTek) 平台使用 BROM (Boot ROM) + DA (Download Agent) 模式进行刷机操作。MultiFlash 支持传统 DA 协议和新版 XFlash 协议。</p><h2 id="支持的芯片" tabindex="-1">支持的芯片 <a class="header-anchor" href="#支持的芯片" aria-label="Permalink to &quot;支持的芯片&quot;">​</a></h2><table tabindex="0"><thead><tr><th>系列</th><th>芯片型号</th></tr></thead><tbody><tr><td>天玑 9000</td><td>MT6983, MT6985</td></tr><tr><td>天玑 8000</td><td>MT6895, MT6896</td></tr><tr><td>天玑 1000</td><td>MT6885, MT6889, MT6893</td></tr><tr><td>天玑 800</td><td>MT6873, MT6875, MT6877</td></tr><tr><td>天玑 700</td><td>MT6833, MT6853</td></tr><tr><td>Helio G</td><td>MT6765, MT6768, MT6769, MT6785</td></tr><tr><td>Helio P</td><td>MT6762, MT6763, MT6771</td></tr><tr><td>其他</td><td>MT6739, MT6761, MT6757</td></tr></tbody></table><h2 id="进入-brom-模式" tabindex="-1">进入 BROM 模式 <a class="header-anchor" href="#进入-brom-模式" aria-label="Permalink to &quot;进入 BROM 模式&quot;">​</a></h2><h3 id="方法一-组合键" tabindex="-1">方法一：组合键 <a class="header-anchor" href="#方法一-组合键" aria-label="Permalink to &quot;方法一：组合键&quot;">​</a></h3><ol><li>完全关机</li><li>取出电池（如可拆卸）</li><li>按住 <strong>音量+</strong> 或 <strong>音量-</strong>（视机型而定）</li><li>保持按住，插入 USB 数据线</li><li>等待电脑识别</li></ol><h3 id="方法二-测试点" tabindex="-1">方法二：测试点 <a class="header-anchor" href="#方法二-测试点" aria-label="Permalink to &quot;方法二：测试点&quot;">​</a></h3><p>部分设备需要短接 BROM 测试点进入下载模式。</p><h2 id="验证连接" tabindex="-1">验证连接 <a class="header-anchor" href="#验证连接" aria-label="Permalink to &quot;验证连接&quot;">​</a></h2><p>设备管理器中应显示：</p><ul><li><code>MediaTek USB Port</code> (COMx)</li><li>或 <code>MediaTek PreLoader USB VCOM</code></li></ul><h2 id="使用-multiflash" tabindex="-1">使用 MultiFlash <a class="header-anchor" href="#使用-multiflash" aria-label="Permalink to &quot;使用 MultiFlash&quot;">​</a></h2><h3 id="_1-选择-mtk-平台" tabindex="-1">1. 选择 MTK 平台 <a class="header-anchor" href="#_1-选择-mtk-平台" aria-label="Permalink to &quot;1. 选择 MTK 平台&quot;">​</a></h3><p>打开 MultiFlash，点击 <strong>MediaTek</strong> 选项卡。</p><h3 id="_2-da-文件" tabindex="-1">2. DA 文件 <a class="header-anchor" href="#_2-da-文件" aria-label="Permalink to &quot;2. DA 文件&quot;">​</a></h3><p>软件内置了常用 DA 文件，也支持自定义：</p><ul><li>点击 <strong>选择 DA</strong> 加载自定义 DA 文件</li><li>或使用内置 DA（推荐）</li></ul><h3 id="_3-连接设备" tabindex="-1">3. 连接设备 <a class="header-anchor" href="#_3-连接设备" aria-label="Permalink to &quot;3. 连接设备&quot;">​</a></h3><ol><li>点击 <strong>连接</strong> 按钮</li><li>让设备进入 BROM 模式</li><li>软件自动检测并显示： <ul><li>HW Code (芯片型号)</li><li>HW Version</li><li>SW Version</li></ul></li></ol><h3 id="_4-操作选项" tabindex="-1">4. 操作选项 <a class="header-anchor" href="#_4-操作选项" aria-label="Permalink to &quot;4. 操作选项&quot;">​</a></h3><h4 id="读取分区" tabindex="-1">读取分区 <a class="header-anchor" href="#读取分区" aria-label="Permalink to &quot;读取分区&quot;">​</a></h4><ol><li>选择要读取的分区</li><li>点击 <strong>读取</strong></li><li>选择保存位置</li></ol><h4 id="刷写分区" tabindex="-1">刷写分区 <a class="header-anchor" href="#刷写分区" aria-label="Permalink to &quot;刷写分区&quot;">​</a></h4><ol><li>选择分区和镜像文件</li><li>点击 <strong>刷写</strong></li><li>等待完成</li></ol><h4 id="格式化" tabindex="-1">格式化 <a class="header-anchor" href="#格式化" aria-label="Permalink to &quot;格式化&quot;">​</a></h4><ol><li>选择要格式化的分区</li><li>点击 <strong>格式化</strong></li><li>确认操作</li></ol><h2 id="scatter-文件" tabindex="-1">Scatter 文件 <a class="header-anchor" href="#scatter-文件" aria-label="Permalink to &quot;Scatter 文件&quot;">​</a></h2><p>MTK 设备通常使用 scatter 文件描述分区布局：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>MT6765_Android_scatter.txt</span></span></code></pre></div><h3 id="加载-scatter" tabindex="-1">加载 Scatter <a class="header-anchor" href="#加载-scatter" aria-label="Permalink to &quot;加载 Scatter&quot;">​</a></h3><ol><li>点击 <strong>加载 Scatter</strong></li><li>选择 scatter 文件</li><li>分区列表自动显示</li></ol><h2 id="常见操作" tabindex="-1">常见操作 <a class="header-anchor" href="#常见操作" aria-label="Permalink to &quot;常见操作&quot;">​</a></h2><h3 id="刷写完整固件" tabindex="-1">刷写完整固件 <a class="header-anchor" href="#刷写完整固件" aria-label="Permalink to &quot;刷写完整固件&quot;">​</a></h3><ol><li>加载 scatter 文件</li><li>选择所有需要刷写的分区</li><li>点击 <strong>下载</strong></li></ol><h3 id="仅刷写-boot" tabindex="-1">仅刷写 Boot <a class="header-anchor" href="#仅刷写-boot" aria-label="Permalink to &quot;仅刷写 Boot&quot;">​</a></h3><ol><li>选择 <code>boot</code> 分区</li><li>选择 <code>boot.img</code> 文件</li><li>点击刷写</li></ol><h3 id="备份-nvram" tabindex="-1">备份 NVRAM <a class="header-anchor" href="#备份-nvram" aria-label="Permalink to &quot;备份 NVRAM&quot;">​</a></h3><ol><li>选择 <code>nvram</code> 分区</li><li>点击 <strong>读取</strong></li><li>保存备份文件</li></ol><div class="warning custom-block"><p class="custom-block-title">重要</p><p>NVRAM 包含 IMEI 等重要信息，刷机前务必备份！</p></div><h2 id="xflash-模式" tabindex="-1">XFlash 模式 <a class="header-anchor" href="#xflash-模式" aria-label="Permalink to &quot;XFlash 模式&quot;">​</a></h2><p>新款 MTK 芯片使用 XFlash 协议：</p><h3 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h3><ul><li>二进制协议，速度更快</li><li>支持更多安全特性</li><li>部分设备强制使用</li></ul><h3 id="使用方法" tabindex="-1">使用方法 <a class="header-anchor" href="#使用方法" aria-label="Permalink to &quot;使用方法&quot;">​</a></h3><ol><li>软件自动检测协议类型</li><li>XFlash 设备会自动使用 XFlash 协议</li><li>操作方式与传统模式相同</li></ol><h2 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h2><h3 id="q-无法进入-brom-模式" tabindex="-1">Q: 无法进入 BROM 模式？ <a class="header-anchor" href="#q-无法进入-brom-模式" aria-label="Permalink to &quot;Q: 无法进入 BROM 模式？&quot;">​</a></h3><ol><li>检查是否完全关机</li><li>尝试取出电池重试</li><li>检查音量键是否正常</li><li>尝试短接测试点</li></ol><h3 id="q-da-加载失败" tabindex="-1">Q: DA 加载失败？ <a class="header-anchor" href="#q-da-加载失败" aria-label="Permalink to &quot;Q: DA 加载失败？&quot;">​</a></h3><ol><li>检查 DA 文件是否匹配芯片</li><li>尝试使用其他 DA 文件</li><li>检查驱动是否正确安装</li></ol><h3 id="q-认证失败" tabindex="-1">Q: 认证失败？ <a class="header-anchor" href="#q-认证失败" aria-label="Permalink to &quot;Q: 认证失败？&quot;">​</a></h3><p>部分设备需要 SLA 认证：</p><ol><li>确认设备是否需要认证</li><li>联系支持获取认证文件</li></ol><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">警告</p><ul><li>刷机前备份 NVRAM 和 重要数据</li><li>确保使用正确的固件版本</li><li>不要随意刷写 <code>preloader</code> 分区</li><li>刷机过程中不要断开连接</li></ul></div><div class="danger custom-block"><p class="custom-block-title">危险分区</p><ul><li><code>preloader</code> - 预加载程序，刷错会变砖</li><li><code>nvram</code> - 包含 IMEI，丢失无法恢复</li><li><code>protect1/2</code> - 保护分区</li><li><code>seccfg</code> - 安全配置</li></ul></div>',58)])])}const m=l(o,[["render",r]]);export{b as __pageData,m as default};
