import{_ as l,o as t,c as e,ag as i}from"./chunks/framework.CbQjVMS6.js";const u=JSON.parse('{"title":"展锐 Spreadtrum 教程","description":"","frontmatter":{},"headers":[],"relativePath":"guide/spd.md","filePath":"guide/spd.md","lastUpdated":1769581884000}'),r={name:"guide/spd.md"};function o(d,a,s,n,h,c){return t(),e("div",null,[...a[0]||(a[0]=[i(`<h1 id="展锐-spreadtrum-教程" tabindex="-1">展锐 Spreadtrum 教程 <a class="header-anchor" href="#展锐-spreadtrum-教程" aria-label="Permalink to &quot;展锐 Spreadtrum 教程&quot;">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p>展锐 (Spreadtrum/UNISOC) 平台使用 BSL (Boot Strap Loader) + FDL (Flash Download) 协议进行刷机操作。</p><h2 id="支持的芯片" tabindex="-1">支持的芯片 <a class="header-anchor" href="#支持的芯片" aria-label="Permalink to &quot;支持的芯片&quot;">​</a></h2><table tabindex="0"><thead><tr><th>系列</th><th>芯片型号</th></tr></thead><tbody><tr><td>虎贲 T7</td><td>T760, T770</td></tr><tr><td>虎贲 T6</td><td>T610, T618, T700, T710, T740</td></tr><tr><td>SC</td><td>SC9863A, SC9832E, SC7731E</td></tr><tr><td>其他</td><td>SC7731, SC8541</td></tr></tbody></table><h2 id="进入下载模式" tabindex="-1">进入下载模式 <a class="header-anchor" href="#进入下载模式" aria-label="Permalink to &quot;进入下载模式&quot;">​</a></h2><h3 id="方法一-组合键" tabindex="-1">方法一：组合键 <a class="header-anchor" href="#方法一-组合键" aria-label="Permalink to &quot;方法一：组合键&quot;">​</a></h3><ol><li>完全关机</li><li>按住 <strong>音量-</strong></li><li>保持按住，插入 USB 数据线</li><li>等待电脑识别</li></ol><h3 id="方法二-工程模式" tabindex="-1">方法二：工程模式 <a class="header-anchor" href="#方法二-工程模式" aria-label="Permalink to &quot;方法二：工程模式&quot;">​</a></h3><p>部分设备可通过拨号盘进入：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>*#*#83781#*#*</span></span></code></pre></div><h2 id="验证连接" tabindex="-1">验证连接 <a class="header-anchor" href="#验证连接" aria-label="Permalink to &quot;验证连接&quot;">​</a></h2><p>设备管理器中应显示：</p><ul><li><code>SPRD U2S Diag</code> (COM端口)</li><li>或 <code>Spreadtrum Phone</code></li></ul><h2 id="使用-sakuraedl" tabindex="-1">使用 SakuraEDL <a class="header-anchor" href="#使用-sakuraedl" aria-label="Permalink to &quot;使用 SakuraEDL&quot;">​</a></h2><h3 id="_1-选择-spreadtrum-平台" tabindex="-1">1. 选择 Spreadtrum 平台 <a class="header-anchor" href="#_1-选择-spreadtrum-平台" aria-label="Permalink to &quot;1. 选择 Spreadtrum 平台&quot;">​</a></h3><p>打开 SakuraEDL，点击 <strong>Spreadtrum</strong> 选项卡。</p><h3 id="_2-连接设备" tabindex="-1">2. 连接设备 <a class="header-anchor" href="#_2-连接设备" aria-label="Permalink to &quot;2. 连接设备&quot;">​</a></h3><ol><li>点击 <strong>连接</strong> 按钮</li><li>让设备进入下载模式</li><li>软件自动检测并显示设备信息</li></ol><h3 id="_3-加载固件" tabindex="-1">3. 加载固件 <a class="header-anchor" href="#_3-加载固件" aria-label="Permalink to &quot;3. 加载固件&quot;">​</a></h3><h4 id="pac-文件-推荐" tabindex="-1">PAC 文件（推荐） <a class="header-anchor" href="#pac-文件-推荐" aria-label="Permalink to &quot;PAC 文件（推荐）&quot;">​</a></h4><ol><li>点击 <strong>加载 PAC</strong></li><li>选择 <code>.pac</code> 固件包</li><li>分区列表自动显示</li></ol><h4 id="单独镜像" tabindex="-1">单独镜像 <a class="header-anchor" href="#单独镜像" aria-label="Permalink to &quot;单独镜像&quot;">​</a></h4><ol><li>选择目标分区</li><li>点击 <strong>选择文件</strong></li><li>选择对应镜像</li></ol><h3 id="_4-刷写操作" tabindex="-1">4. 刷写操作 <a class="header-anchor" href="#_4-刷写操作" aria-label="Permalink to &quot;4. 刷写操作&quot;">​</a></h3><ol><li>勾选要刷写的分区</li><li>点击 <strong>开始下载</strong></li><li>等待完成</li></ol><h2 id="pac-文件说明" tabindex="-1">PAC 文件说明 <a class="header-anchor" href="#pac-文件说明" aria-label="Permalink to &quot;PAC 文件说明&quot;">​</a></h2><p>PAC 是展锐的官方固件格式，包含：</p><ul><li>分区表信息</li><li>所有分区镜像</li><li>刷机配置</li></ul><h3 id="pac-文件结构" tabindex="-1">PAC 文件结构 <a class="header-anchor" href="#pac-文件结构" aria-label="Permalink to &quot;PAC 文件结构&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>firmware.pac</span></span>
<span class="line"><span>├── fdl1.bin      # 第一阶段加载器</span></span>
<span class="line"><span>├── fdl2.bin      # 第二阶段加载器</span></span>
<span class="line"><span>├── boot.img      # 内核</span></span>
<span class="line"><span>├── system.img    # 系统</span></span>
<span class="line"><span>├── vendor.img    # 厂商</span></span>
<span class="line"><span>├── userdata.img  # 用户数据</span></span>
<span class="line"><span>└── ...</span></span></code></pre></div><h2 id="常见操作" tabindex="-1">常见操作 <a class="header-anchor" href="#常见操作" aria-label="Permalink to &quot;常见操作&quot;">​</a></h2><h3 id="刷写完整固件" tabindex="-1">刷写完整固件 <a class="header-anchor" href="#刷写完整固件" aria-label="Permalink to &quot;刷写完整固件&quot;">​</a></h3><ol><li>加载 PAC 文件</li><li>勾选所有分区</li><li>点击下载</li></ol><h3 id="刷写单个分区" tabindex="-1">刷写单个分区 <a class="header-anchor" href="#刷写单个分区" aria-label="Permalink to &quot;刷写单个分区&quot;">​</a></h3><ol><li>选择目标分区</li><li>选择镜像文件</li><li>点击刷写</li></ol><h3 id="读取分区" tabindex="-1">读取分区 <a class="header-anchor" href="#读取分区" aria-label="Permalink to &quot;读取分区&quot;">​</a></h3><ol><li>选择要备份的分区</li><li>点击 <strong>读取</strong></li><li>选择保存位置</li></ol><h3 id="擦除数据" tabindex="-1">擦除数据 <a class="header-anchor" href="#擦除数据" aria-label="Permalink to &quot;擦除数据&quot;">​</a></h3><ol><li>选择 <code>userdata</code> 分区</li><li>点击 <strong>擦除</strong></li></ol><h2 id="fdl-地址配置" tabindex="-1">FDL 地址配置 <a class="header-anchor" href="#fdl-地址配置" aria-label="Permalink to &quot;FDL 地址配置&quot;">​</a></h2><p>不同芯片的 FDL 加载地址不同：</p><table tabindex="0"><thead><tr><th>芯片</th><th>FDL1 地址</th><th>FDL2 地址</th></tr></thead><tbody><tr><td>T760</td><td>0x5500</td><td>0x9EFFFE00</td></tr><tr><td>T618</td><td>0x5500</td><td>0x8E000000</td></tr><tr><td>SC9863A</td><td>0x5500</td><td>0x8E000000</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">提示</p><p>SakuraEDL 会自动检测芯片并配置正确的地址，一般无需手动设置。</p></div><h2 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h2><h3 id="q-设备无法识别" tabindex="-1">Q: 设备无法识别？ <a class="header-anchor" href="#q-设备无法识别" aria-label="Permalink to &quot;Q: 设备无法识别？&quot;">​</a></h3><ol><li>检查驱动是否安装</li><li>尝试不同的 USB 端口</li><li>检查是否正确进入下载模式</li></ol><h3 id="q-下载失败" tabindex="-1">Q: 下载失败？ <a class="header-anchor" href="#q-下载失败" aria-label="Permalink to &quot;Q: 下载失败？&quot;">​</a></h3><ol><li>检查固件是否匹配设备</li><li>尝试重新进入下载模式</li><li>检查 USB 数据线</li></ol><h3 id="q-pac-加载失败" tabindex="-1">Q: PAC 加载失败？ <a class="header-anchor" href="#q-pac-加载失败" aria-label="Permalink to &quot;Q: PAC 加载失败？&quot;">​</a></h3><ol><li>确认 PAC 文件完整</li><li>检查文件是否损坏</li><li>尝试重新下载固件</li></ol><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">警告</p><ul><li>刷机前备份重要数据</li><li>使用正确的固件版本</li><li>不要中断刷机过程</li><li>确保电池电量充足</li></ul></div><div class="danger custom-block"><p class="custom-block-title">危险分区</p><ul><li><code>fdl1</code> / <code>fdl2</code> - 加载程序</li><li><code>spl</code> - 二级引导</li><li><code>nvitem</code> - NV 数据</li><li><code>phasecheck</code> - 校准数据</li></ul></div>`,54)])])}const b=l(r,[["render",o]]);export{u as __pageData,b as default};
